({
start: async function(name, description, code, tags){
       try{
            let flowData = {
                name: name,
                description:description,
                id: webSkel.servicesRegistry.UtilsService.generateId(),
                content:code,
                tags:tags
            }
            await webSkel.currentUser.space.addFlow(flowData);
            this.return(flowData);
        }catch(e){
            this.fail(e);
            }
    }
})
