content:
({
start: function(ideas, documentId, details){
let document = webSkel.currentUser.space.getDocument(documentId);
this.prompt = `${details.prompt || "Please generate chapter titles based on the following array of ideas"}: "${ideas}".Generate ${details.nr || "one title per idea"}. The response should have the following structure: {"titles":["chapter title 1", "chapter title 2", ... ,"chapter title n"]}.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute(document);
},
execute: async function(document){
let titles = await this.request(this.prompt);
try{
 let titlesObj = JSON.parse(titles);
 await document.addEmptyChapters(titlesObj);
 await webSkel.changeToDynamicPage("manage-chapters-page",`documents/${document.id}/manage-chapters-page`);

}catch(e){
this.fail(e);
}
this.return(titles);
}
})
