start: async function(title, text){
       try{
            let announcementData={
                title:title,
                text:text,
                date: new Date().toISOString().split('T')[0],
            };
            await webSkel.currentUser.space.addAnnouncement(announcementData);
            this.return(announcementData);
        }catch(e){
            this.fail(e);
            }
    }
})
