({
start: function(userPrompt){
   let agent = webSkel.currentUser.space.agent;
   let context = `You are a custom GPT agent designed for specific tasks in a software application. You have tried to find an operation in the application to execute to answer to the user's needs but you have failed to find any. In this case you should improvise an answer that is satisfactory. In case you cannot find an answer apologize and inform the user of your configuration and purpose`;
    agent.addMessage("system", context);
    this.prompt = userPrompt;
    this.setDefaultValues();
    this.setIntelligenceLevel(3);
    this.execute(agent);
    },
execute: async function(agent){
	let response = await this.chatbot(this.prompt, "", agent.conversationHistory);
	this.return(response);
    }
})
