({
start: function(request){
	let agent = webSkel.currentUser.space.agent;
        let agentFlows = webSkel.currentUser.space.flows.filter((flow)=>flow.tags.includes("agents"));
        let operations = agentFlows.filter((flow) => flow.agentConfigs)
            .map((flow) => ({
                id: flow.id,
                description: flow.agentConfigs.description,
            }));
	let context = `You are a custom GPT agent designed for specific tasks in a software application. Your purpose right now is to figure out what operation the user is trying to accomplish using your help. Here is a list of operations that you are capable of doing and their ID's: ${JSON.stringify(operations)}. Using only this list, figure out what operation the user is trying to do. If you found an operation that matches the user's request, your response should look like this: {"operation": boolean value, "operationId": "id of the matching operation"}. If none of these operations match the user's request, set the "operation" attribute to false.`;
	agent.addMessage("system", context);
	this.prompt = request;
    this.setDefaultValues();
    this.setIntelligenceLevel(3);
    this.setResponseFormat("json_object");
    this.execute(agent);
    },
execute: async function(agent){
	let response = await this.chatbot(this.prompt, "", agent.getContext());
	try{
		JSON.parse(response);
	}catch(e){
		this.fail(e);
	}
    this.return(response);
    }
})
