({
start: function(request){
	let agent = webSkel.currentUser.space.agent;
        let agentFlows = webSkel.currentUser.space.flows.filter((flow)=>flow.tags.includes("agents"));
        let operations = agentFlows.filter((flow) => flow.agentConfigs)
            .map((flow) => ({
                id: flow.id,
                description: flow.agentConfigs.description,
            }));
	this.prompt = `You are a custom GPT agent designed for specific tasks in a software application. Your purpose right now is to figure out what operation the user is trying to accomplish using your help. Here is a list of operations that you are capable of doing and their ID's: ${JSON.stringify(operations)}. Using only this list, figure out what operation the user is trying to do. Here's what the user said:  ${request}. If you found an operation that matches the user's request, your response should look like this: {"operation": true, "operationId": "id of the matching operation"}. If none of these operations match the user's request, answer in a humanly manner based on your capabilities, rules and knowledge that are listed here and define your purpose: ${agent.loadKnowledge()}.`
    this.setDefaultValues();
    this.setIntelligenceLevel(3);
    this.execute(agent);
    },
execute: async function(agent){
	let response = await this.chatbot(this.prompt, "", agent.conversationHistory);
	try{
		JSON.parse(response);
	}catch(e){
		this.fail(e);
	}
    this.return(response);
    }
})
