({
start: function(...args){
this.prompt = `Please suggest a chapter based on the following text: "${args[0]}". The answer should have the following structure: {title:"chapter title", paragraphs: ["paragraph 1 text", "paragraph 2 text"... "paragraph n text"]}`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();

},
execute: async function(){
let altChapter = await this.brainstorm(this.prompt, 1);
try{
JSON.parse(altChapter);
}catch(e){
this.fail(e);
}
this.return(altChapter);
}
})
