({
start: function(prompt, personalityName, personalityDescription, replyHistory){
this.prompt = `Step into the shoes of ${personalityName}, a character known for their distinctive traits: ${personalityDescription}. Your mission is to respond to the given text: "${prompt}" and showing different emotions while embodying the essence of this unique personality. The response should have the following structure: {"reply": "reply to the given text", "emotions":[{"name":"name of emotion 1", "emoji":"emoji of emotion 1"}, {"name":"name of emotion 2", "emoji":"emoji of emotion 2"}... {"name":"name of emotion n", "emoji":"emoji of emotion n"}]}`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.setThink("Thinking...");
this.execute(replyHistory);
},
execute: async function(replyHistory){
let text = await this.chatbot(this.prompt, "", replyHistory);
this.return(text);
}
})
