({
start: function(...args){
this.prompt = `Please suggest an idea based on the following topic: "${args[0]}". Return only the idea without quotation marks.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let alternativeTitles = await this.brainstorm(this.prompt, 5, 20);
try{
JSON.parse(alternativeTitles);
}catch(e){
this.fail(e);
}
this.return(alternativeTitles);
}
})
