({
start: function(text){
this.prompt = `Please suggest an improved version of the following text: "${text}". Improve it both semantically and grammatically. Return only the text as a string without quotation marks`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let text = await this.proofread("", this.prompt);
this.return(text);
}
})
