({
start: function(ideas){
this.prompt = `Please suggest a title for a chapter that has the following main ideas: "${ideas}". Return only the title without quotation marks.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let alternativeTitles = await this.brainstorm(this.prompt, 10, 10);
try{
JSON.parse(alternativeTitles);
}catch(e){
this.fail(e);
}
this.return(alternativeTitles);
}
})
