({
start: function(replyHistory){
this.prompt = `Please summarize the following conversation: "${replyHistory}" by creating the main idea that each role has. the response should have the following structure: {"summary":[{role:"user", mainIdea:"main idea of user replies"}, {role:"assitant", mainIdea:"main idea of assistant replies"}]}.`;
this.setThink("");
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let summary = await this.request(this.prompt);
this.return(summary);
}
})
