content:
({
start: function(...args){
this.prompt = `Please generate chapters based on the following array of ideas: "${args[0]}". Please put all chapters in an array. Generate one chapter per idea. A chapter should have the following structure:{title:"Chapter Title", paragraphs:[{text:"paragraph 1 text", mainIdea:"paragraph 1 summary"}, {text:"paragraph 2 text", mainIdea:"paragraph 2 summary"}, ... {text:"paragraph n text", mainIdea:"paragraph n summary"}]}.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let chapters = await this.request(this.prompt);
try{
JSON.parse(chapters);
}catch(e){
this.fail(e);
}
this.return(chapters);
}
})
