({
start: function(...args){
this.prompt = `Please summarize the following text: "${args[0]}" by creating a JSON array of strings containing the main ideas of the text. Create one idea per chapter if the given text contains any chapters.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let ideas = await this.request(this.prompt);
this.return(ideas);
}
})
