content:
({
start: function(idea) {
this.prompt = `Please suggest a chapter based on the following idea: "${idea}". The answer should have the following structure:
{
"title": "A fitting chapter title for the content",
"id": "randomly generated 12 length base 56 string",
"visibility": "show",
"paragraphs": [
{
"text": "Text for this paragraph",
"id": "randomly generated 12 length base 56 string",
"mainIdea": "The main idea for this paragraph",
"alternativeParagraphs": []
},
...additional paragraphs in similar format...
],
"alternativeChapters": [],
"mainIdeas": ["Main idea 1 for the chapter", ...additional main ideas...]
}`;

this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},

execute: async function() {
let altChapter = await this.brainstorm(this.prompt, 1);
try {
JSON.parse(altChapter);
} catch (e) {
this.fail(e);
}
this.return(altChapter);
}
})
