({
start: function(...args){
this.prompt = `Please suggest an improved version of the following text: "${args[0]}". Improve it both semantically and grammatically. Return only the text`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let altAbstract = await this.proofread("", this.prompt);
this.return(altAbstract);
}
})
