({
start: function(ideas){
this.prompt = `Please generate paragraphs based on the following array of ideas: "${ideas}". Please put all paragraphs in an array. Generate one paragraph per idea. A paragraph should have the following structure: "paragraph text".`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let paragraphs = await this.request(this.prompt);
try{
JSON.parse(paragraphs);
}catch(e){
this.fail(e);
}
this.return(paragraphs);
}
})
