({
start: function(text, personalityId, language, details, maxTokens){
if(personalityId){
  let personality = webSkel.currentUser.space.getPersonality(personalityId);
  this.prompt = `Please translate the following text: "${text}" in the following language: "${language}". 
  ${details.prompt || ""}. You will play the role of this personality: "${personality.name}", which has the 
  following characteristics: "${personality.description}". You will translate the given text in the style of the 
  given personality. Return only the translated text.`;
}else{
  this.prompt = `Please translate the following text: "${text}" in the following language: "${language}". 
  ${details.prompt || ""}. Return only the translated text.`;
}

this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute(maxTokens);
},
execute: async function(maxTokens){
let text = await this.request(this.prompt, maxTokens);
this.return(text);
}
})
