({
start: function(title, topic, chaptersCount, personalityId, maxTokens){
let personalityPrompt;
if(personalityId){
let personality = webSkel.currentUser.space.getPersonality(personalityId);
personalityPrompt = `Step into the shoes of ${personality.name}, a character known for their distinctive traits: ${personality.description}. Your mission is to create a document in such a way that it encapsulates the distinct essence of this character. `;
}
this.prompt = `${personalityPrompt || ""}Please generate a document based on the following topic: "${topic}" and title: "${title}". The document should have ${chaptersCount} chapters. The response should have the following structure: {"title": "${title}", "topic":"${topic}", "abstract": "abstract of the document", "mainIdeas":["idea 1 of the document", "idea 2 of the document", ... , "idea ${chaptersCount} of the document"], "chapters": [{chapter 1}, {chapter 2},..., {chapter ${chaptersCount}}]}. A chapter should have the following structure:{"title":"Chapter Title", "mainIdeas":["idea 1", "idea 2", ... , idea n"], "paragraphs":[{text:"paragraph 1 text", mainIdea:"paragraph 1 summary"}, {text:"paragraph 2 text", mainIdea:"paragraph 2 summary"}, ... {text:"paragraph n text", mainIdea:"paragraph n summary"}]}.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let document = await this.request(this.prompt);
try{
let docObj = JSON.parse(document);
await webSkel.currentUser.space.addDocument(docObj);
}catch(e){
this.fail(e);
}
this.return(document);
}
})
