({
start: async function(documentId, chapterId, paragraphId, alternativeParagraphId){
	try{
		let document = webSkel.currentUser.space.getDocument(documentId);
		let chapter = document.getChapter(chapterId);
		let paragraph = chapter.getParagraph(paragraphId);
		let alternativeParagraph = paragraph.getAlternativeParagraph(alternativeParagraphId);

		let currentParagraphId = paragraph.id;
        	let currentParagraphText = paragraph.text;
        	let currentParagraphMainIdea = paragraph.mainIdea;

        	let alternativeParagraphIndex = paragraph.alternativeParagraphs.findIndex(paragraph => paragraph.id === alternativeParagraph.id);
        	paragraph.select(alternativeParagraph);
        	paragraph.alternativeParagraphs.splice(alternativeParagraphIndex, 1);
        	paragraph.alternativeParagraphs.splice(alternativeParagraphIndex,0,{
            		id: currentParagraphId,
            		text: currentParagraphText,
            		mainIdea: currentParagraphMainIdea
        	});
        	await documentFactory.updateDocument(webSkel.currentUser.space.id, document);
		this.return(alternativeParagraphId);
	}catch(e){
		this.fail(e);
		}
	}
})
