({
start: function(prompt, personalityName, personalityDescription, replyHistory){
this.prompt = `Step into the shoes of ${personalityName}, a character known for their distinctive traits: ${personalityDescription}. Your mission is to respond to the given text: "${prompt}" while emanating an emotion that naturally aligns with the text and encapsulates the distinct essence of this character. Please answer in the same language as the given text. Please only use emojis that have faces. The response should have the following structure: {"reply": "reply to the given text", "emotion":{"name":"name of the emotion", "emoji":"emoji of the emotion"}}`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.setThink("Thinking...");
this.execute(replyHistory);
},
execute: async function(replyHistory){
let text = await this.chatbot(this.prompt, "", replyHistory);
this.return(text);
}
})
