({
start: function(documentId, chapterId, paragraphId, details, maxTokens){
let document = webSkel.currentUser.space.getDocument(documentId);
let chapter = document.getChapter(chapterId);
this.prompt = `${details.prompt || "Please suggest a paragraph that fits in the following chapter:"} "${JSON.stringify(chapter.simplifyChapter())}". The result should have the following structure: {"text": "paragraph text", "mainIdea": "main idea of the paragraph"}.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute(maxTokens);
},
execute: async function(maxTokens){
let altParagraph = await this.request(this.prompt, maxTokens);
try{
JSON.parse(altParagraph);
}catch(e){
this.fail(e);
}
this.return(altParagraph);
}
})
