({
start: function(ideas){
this.prompt = `Please generate chapter titles based on the following array of ideas: "${ideas}".Generate one title per idea. The response should have the following structure: {"titles":["chapter title 1", "chapter title 2", ... ,"chapter title n"]}.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let titles = await this.request(this.prompt);
try{
JSON.parse(titles);
}catch(e){
this.fail(e);
}
this.return(titles);
}
})
