({
start: function(currentParagraph){
this.prompt = `Please suggest a paragraph based on the following text: "${currentParagraph}". The result should have the following structure: {"text": "paragraph text", "mainIdea": "main idea of the paragraph"}.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();

},
execute: async function(){
let altParagraph = await this.brainstorm(this.prompt, 1);
try{
JSON.parse(altParagraph);
}catch(e){
this.fail(e);
}
this.return(altParagraph);
}
})
