({
start: function(text, personalityName, personalityDescription, language){
this.prompt = `Please translate the following text: "${text}" in the following language: "${language}". You will play the role of this personality: "${personalityName}", which has the following characteristics: "${personalityDescription}". You will translate the given text in the style of the given personality. Return only the translated text.`;
this.setDefaultValues();
this.setIntelligenceLevel(3);
this.execute();
},
execute: async function(){
let text = await this.proofread("", this.prompt);
this.return(text);
}
})
